
public class Computer {

	
	// Instance Variables
	private String make;
	private String model;
	private double price;
	
	//Constructor - can be automatically generated. 
	public Computer(String make, String model, double price) {
		// super();  --> used the Source-> Generate Constructor to create the constructor. It automatically put "super();". The parent of this class is object. 
		this.make = make;
		this.model = model;
		this.price = price;
	}
	
	
	
}
